import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import InputMentions from 'react-native-input-mentions';

export default function App() {
  const users = [
    { name: 'Wiloke Team', id: 1, avatar: '1' },
    { name: 'Nguyen Long', id: 2, avatar: '2' },
    { name: 'Wilcity', id: 3, avatar: '3' },
  ];

  return (
    <View style={styles.container}>
      <InputMentions
        users={users}
        keyExtractor={item => String(item.id)}
        renderUserItem={user => {
          return <Text>{user.name}</Text>;
        }}
        keyForMention="name"
        value="abcd Nguyen Long"
        entityMap={[
          {
            mentions: { name: 'Nguyen Long', id: 2, avatar: '2' },
            range: { offset: 5, length: 11 },
          },
        ]}
        onChange={console.log}
        inputContainerStyle={{
          borderWidth: 1,
        }}
        userContainerStyle={{
          borderWidth: 1,
          borderColor: 'red',
        }}
      />

      <InputMentions
        readonly
        value="demo readonly Nguyen Long"
        keyForMention="name"
        entityMap={[
          {
            mentions: { name: 'Nguyen Long', id: 2, avatar: '2', link: '2' },
            range: { offset: 14, length: 11 },
          },
        ]}
        inputContainerStyle={{
          borderWidth: 1,
        }}
        mentionStyle={{
          backgroundColor: 'transparent',
          fontWeight: '700',
        }}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
